//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.18;

contract L1Contract {
    address l2Messenger = 0xb75d7e84517e1504C151B270255B087Fd746D34C;

    event SendMessage(
        uint256 tokenId, 
        address _to
    );

    constructor() {}

    function sendMessage(uint256 _tokenId) public {
        emit SendMessage(_tokenId, l2Messenger);
    }
}

