pragma solidity ^0.8.0;

import "./IBridge.sol";

contract L2Contract {
    string private greeting;
    address l1Contract;
    address l2Bridge;

    constructor(address _l1Contract, address _l2Bridge) {
      l1Contract = _l1Contract;
      l2Bridge = _l2Bridge;
    }

    function greet() public view returns (string memory) {
        return greeting;
    }

    function setGreeting(string memory _greeting) public {
        IBridge bridge = IBridge(l2Bridge);
        address sender = bridge.sender();
        require(sender == l1Contract);
        greeting = _greeting;
    }
}","keccak256":"0x4957a8b993a8abed35e1f210514dc2e66338ebac1edb08c28476a81fc9c65f55","license":"MIT"}},"version":1}
